let celdas = flow.get("celdas") || [
    { id: 1, estado: "parada", unid_ok_hora: 0, unid_ok_total_turno: 0, tiempo_ciclo: 10, utilizacion: 0, tiempo_espera: 0, horas_trabajadas: 0 },
