// msg.payload = { turno: 1, meta: X, real: Y, ... }
let p = msg.payload;
let category = `T${p.turno}`; // La etiqueta para el eje X, ej: "T1"

// Mensaje para la barra "Real"
let msg_real = {
    topic: "Producci√≥n Real",
    payload: {
        x: category,
        y: p.real
    }
};

// Mensaje para la barra "Meta"
let msg_meta = {
    topic: "Meta de Turno",
    payload: {
        x: category,
        y: p.meta
    }
};

// Retorna un array de mensajes.
// El ui-chart los recibir√° y los plotear√° como dos barras
// en la misma categor√≠a (ej. "T1")
return [msg_real, msg_meta];

// Motor de Simulaci√≥n (Gemelo Digital) - reemplazar c√≥digo actual por este

const PROCESOS = ['corte','costura','inspeccion','empaque'];

let celdas = flow.get("celdas");
let estado_sim = flow.get("estado_sim");

// Inicializaci√≥n si no existe
if (!celdas) {
    celdas = [];
    for (let i=1;i<=6;i++){
        celdas.push({
            id: i,
            estado: "parada",
            unid_ok_hora: 0,
            unid_ok_total_turno: 0,
            tiempo_ciclo: 10 + (i%4)*2,
            utilizacion: 0,
            tiempo_espera: 0,
            horas_trabajadas: 0,
            proceso: 'corte'
        });
    }
}
if (!estado_sim) {
    estado_sim = {
        hora_turno: 0,
        num_turno: 1,
        horas_totales: 0,
        real_acumulado: 0
    };
}

// --- Par√°metros del dashboard (vienen por flow.*) ---
let staffing = flow.get("staffing");
if (typeof staffing !== "number" || staffing < 0) staffing = 3;

let meta_hora_por_celda = flow.get("meta_hora");
if (typeof meta_hora_por_celda !== "number" || meta_hora_por_celda <= 0) meta_hora_por_celda = 5;

// Avanzar simulaci√≥n 1 hora
estado_sim.hora_turno++;
estado_sim.horas_totales++;

// c√°lculos por hora
let produccion_total_hora = 0;
if (staffing > celdas.length) staffing = celdas.length;

for (let i=0; i<celdas.length; i++){
    let celda = celdas[i];
    celda.unid_ok_hora = 0;

    if (i < staffing) {
        let r = Math.random();
        if (r < 0.08) {
            celda.estado = 'parada';
            celda.progreso = 0;
        } else if (r < 0.18) {
            celda.estado = 'esperando';
            celda.tiempo_espera = (celda.tiempo_espera || 0) + 1;
        } else {
            celda.estado = 'trabajando';
            celda.horas_trabajadas = (celda.horas_trabajadas || 0) + 1;
            let variationFactor = 0.7 + Math.random()*0.6;
            let unidades = Math.round(meta_hora_por_celda * variationFactor);
            if (unidades < 0) unidades = 0;
            celda.unid_ok_hora = unidades;
            celda.unid_ok_total_turno = (celda.unid_ok_total_turno || 0) + unidades;
            celda.progreso = ((celda.progreso || 0) + Math.round( (unidades / Math.max(1, meta_hora_por_celda)) * 100 )) % 100;
        }
    } else {
        celda.estado = 'sin_personal';
        celda.progreso = 0;
    }

    if (estado_sim.hora_turno > 0) {
        celda.utilizacion = Math.min(100, ((celda.horas_trabajadas || 0) / estado_sim.hora_turno) * 100);
    } else {
        celda.utilizacion = 0;
    }

    produccion_total_hora += (celda.unid_ok_hora || 0);
}

estado_sim.real_acumulado = (estado_sim.real_acumulado || 0) + produccion_total_hora;

// Preparar salidas
let msg_grid = { payload: celdas };

let meta_hora_total_staffed = meta_hora_por_celda * staffing;
let msg_tabla = {
    payload: {
        turno: estado_sim.num_turno,
        hora: estado_sim.hora_turno,
        personal: staffing,
        meta_por_hora: meta_hora_total_staffed,
        produccion_actual: produccion_total_hora,
        meta_acumulada: meta_hora_total_staffed * estado_sim.hora_turno,
        real_acumulado: estado_sim.real_acumulado
    }
};

let msg_turno_fin = null;
if (estado_sim.hora_turno >= 8) {
    let meta_total_turno = meta_hora_total_staffed * 8;
    let real_total_turno = estado_sim.real_acumulado;
    let cumplimiento = (meta_total_turno > 0) ? (real_total_turno / meta_total_turno) * 100 : 0;

    msg_turno_fin = {
        payload: {
            turno: estado_sim.num_turno,
            meta: meta_total_turno,
            real: real_total_turno,
            cumplimiento: cumplimiento,
            alerta_msg: cumplimiento < 90 ? `ALERTA: Desviaci√≥n en Turno ${estado_sim.num_turno}. Cumplimiento: ${cumplimiento.toFixed(1)}%` : `Turno ${estado_sim.num_turno} OK. Cumplimiento: ${cumplimiento.toFixed(1)}%`,
            hubo_desviacion: cumplimiento < 90
        }
    };

    // Reset turno
    estado_sim.hora_turno = 0;
    estado_sim.num_turno++;
    estado_sim.real_acumulado = 0;

    for (let celda of celdas) {
        celda.unid_ok_total_turno = 0;
        celda.horas_trabajadas = 0;
        celda.tiempo_espera = 0;
        celda.utilizacion = 0;
        celda.progreso = 0;
        celda.proceso = 'corte';
        celda.estado = 'parada';
    }
}

// Mensaje de configuraci√≥n expl√≠cita (ahora con staffing y meta)
let msg_staffing_config = {
    payload: celdas.length,
    numCeldas: celdas.length,
    topic: "config",
    staffing: staffing,
    meta_hora: meta_hora_por_celda
};

flow.set("celdas", celdas);
flow.set("estado_sim", estado_sim);

// Salidas: [grid, tabla, turno_fin, staffing_config]
return [ msg_grid, msg_tabla, msg_turno_fin,msg_staffing_config ];

initialize:
// C√≥digo de inicializaci√≥n (opcional)
flow.set("celdas", undefined);
flow.set("estado_sim", undefined);

func:
// Function node -> Default Meta: 5 (s√≥lo si no existe)
let currentMeta = flow.get('meta_hora');
if (typeof currentMeta !== 'number' || isNaN(currentMeta)) {
  flow.set('meta_hora', 5);
  msg.payload = 5;
  msg.topic = 'meta';
  msg.meta_hora = 5;
  return msg;
}
return null;

// Function node -> Default Staffing: 3 (s√≥lo si no existe)
let current = flow.get('staffing');
if (typeof current !== 'number' || isNaN(current)) {
  flow.set('staffing', 3);
  msg.payload = 3;
  msg.topic = 'staffing';
  msg.staffing = 3;
  return msg;
}
return null;

// --- 1. OBTENER ESTADO ANTERIOR (O INICIALIZAR) ---
const PROCESOS = ['corte', 'costura', 'inspeccion', 'empaque'];

let celdas = flow.get("celdas") || [
    { id: 1, estado: "parada", unid_ok_hora: 0, unid_ok_total_turno: 0, tiempo_ciclo: 10, utilizacion: 0, tiempo_espera: 0, horas_trabajadas: 0, proceso: 'corte', progreso: 0 },
    { id: 2, estado: "parada", unid_ok_hora: 0, unid_ok_total_turno: 0, tiempo_ciclo: 12, utilizacion: 0, tiempo_espera: 0, horas_trabajadas: 0, proceso: 'corte', progreso: 0 },
    { id: 3, estado: "parada", unid_ok_hora: 0, unid_ok_total_turno: 0, tiempo_ciclo: 8, utilizacion: 0, tiempo_espera: 0, horas_trabajadas: 0, proceso: 'corte', progreso: 0 },
    { id: 4, estado: "parada", unid_ok_hora: 0, unid_ok_total_turno: 0, tiempo_ciclo: 10, utilizacion: 0, tiempo_espera: 0, horas_trabajadas: 0, proceso: 'corte', progreso: 0 },
    { id: 5, estado: "parada", unid_ok_hora: 0, unid_ok_total_turno: 0, tiempo_ciclo: 15, utilizacion: 0, tiempo_espera: 0, horas_trabajadas: 0, proceso: 'corte', progreso: 0 },
    { id: 6, estado: "parada", unid_ok_hora: 0, unid_ok_total_turno: 0, tiempo_ciclo: 10, utilizacion: 0, tiempo_espera: 0, horas_trabajadas: 0, proceso: 'corte', progreso: 0 }
];
let estado_sim = flow.get("estado_sim") || { hora_turno: 0, num_turno: 1, horas_totales: 0 };

// --- 2. OBTENER PAR√ÅMETROS DEL DASHBOARD ---
let staffing = flow.get("staffing") || 3;
let meta_hora_por_celda = flow.get("meta_hora") || 5;

// --- 3. AVANZAR SIMULACI√ìN 1 HORA ---
estado_sim.hora_turno++;
estado_sim.horas_totales++;

let produccion_total_hora = 0;
let produccion_total_turno_acumulada = 0;

// --- 4. SIMULACI√ìN POR CELDA (Flujo, KPIs) ---
for (let i = 0; i < celdas.length; i++) {
    let celda = celdas[i];
    celda.unid_ok_hora = 0; // Reseteamos la producci√≥n de esta hora

    // 4.1. Reasignaci√≥n de Personal
    if (i < staffing) {

        // 4.2. Simulaci√≥n Cuello de Botella
        if (celda.id === 2 && celdas[0].estado !== 'trabajando') {
            celda.estado = 'esperando';
            celda.tiempo_espera += 1;
            celda.progreso = 0; // Pausa y resetea progreso si espera
        } 
        // 4.3. Simulaci√≥n de Falla Aleatoria
        else if (celda.estado !== 'esperando') {
            let rand = Math.random();
            if (celda.estado === 'parada' && rand < 0.2) {
                celda.estado = 'trabajando'; // 20% chance de repararse
            }

            if (celda.estado === 'trabajando') {
                if (rand < 0.15) { // 15% chance de falla
                    celda.estado = 'parada';
                    celda.progreso = 0; // Resetea progreso al fallar
                } else {
                    // --- L√ìGICA DE PROCESO TEXTIL ---
                    celda.progreso += 25; // Avanza 25% por "hora" (tick)
                    celda.horas_trabajadas += 1;

                    if (celda.progreso >= 100) {
                        celda.progreso = 0;
                        let idx_actual = PROCESOS.indexOf(celda.proceso);

                        if (idx_actual >= PROCESOS.length - 1) { // Si estaba en 'empaque'
                            // Proceso terminado, 1 unidad producida
                            celda.proceso = PROCESOS[0]; // Vuelve a 'corte'
                            celda.unid_ok_hora = 1; 
                            celda.unid_ok_total_turno += 1;
                        } else {
                            celda.proceso = PROCESOS[idx_actual + 1]; // Avanza al siguiente proceso
                        }
                    }
                    // --- FIN L√ìGICA TEXTIL ---
                }
            } else if (celda.estado === 'parada') {
                // Sigue en parada, no hace nada
            }
        }
    } else {
        // Celda NO tiene personal
        celda.estado = 'sin_personal';
        celda.progreso = 0;
    }

    // 4.4. Calcular KPIs de Celda
    if (estado_sim.hora_turno > 0) {
        celda.utilizacion = (celda.horas_trabajadas / estado_sim.hora_turno) * 100;
    } else {
        celda.utilizacion = 0;
    }
    produccion_total_turno_acumulada += celda.unid_ok_total_turno;
    produccion_total_hora += celda.unid_ok_hora;
}

// --- 5. PREPARAR SALIDAS (Dashboard) ---

// Salida 1: Datos para el Grid de Celdas (se env√≠a siempre)
let msg_grid = { payload: celdas };

// Salida 2: Datos para la Tabla Comparativa (se env√≠a siempre)
let meta_hora_total_staffed = meta_hora_por_celda * staffing;
let msg_tabla = { 
    payload: {
        hora: estado_sim.hora_turno,
        turno: estado_sim.num_turno,
        staffing: staffing,
        meta_hora: meta_hora_total_staffed,
        real_hora: produccion_total_hora,
        meta_turno_acum: meta_hora_total_staffed * estado_sim.hora_turno,
        real_turno_acum: produccion_total_turno_acumulada
    }
};

let msg_turno_fin = null;

// --- 6. AGREGACI√ìN POR TURNO (Flujo) ---
if (estado_sim.hora_turno >= 8) { // Asumimos turno de 8 horas
    let meta_total_turno = meta_hora_total_staffed * 8;
    let real_total_turno = produccion_total_turno_acumulada;
    let cumplimiento = 0;
    if (meta_total_turno > 0) {
        cumplimiento = (real_total_turno / meta_total_turno) * 100;
    }

    // 6.1. Preparar Salida 3: Datos de fin de turno (Gr√°fico y Alerta)
    msg_turno_fin = {
        payload: {
            turno: estado_sim.num_turno,
            meta: meta_total_turno,
            real: real_total_turno,
            cumplimiento: cumplimiento,
            alerta_msg: cumplimiento < 90 ? 
                `ALERTA: Desviaci√≥n en Turno ${estado_sim.num_turno}. Cumplimiento: ${cumplimiento.toFixed(1)}%` : 
                `Turno ${estado_sim.num_turno} OK. Cumplimiento: ${cumplimiento.toFixed(1)}%`,
            hubo_desviacion: cumplimiento < 90
        }
    };

    // 6.3. Resetear para el siguiente turno
    estado_sim.hora_turno = 0;
    estado_sim.num_turno++;
    for (let celda of celdas) {
        celda.unid_ok_total_turno = 0;
        celda.horas_trabajadas = 0;
        celda.tiempo_espera = 0;
        celda.utilizacion = 0;
        celda.progreso = 0;
        celda.proceso = 'corte';
    }
}

// --- 7. GUARDAR ESTADO PARA PR√ìXIMO TICK ---
flow.set("celdas", celdas);
flow.set("estado_sim", estado_sim);
// Al final del nodo "Motor de Simulaci√≥n", antes del return
let msg_staffing_config = { numCeldas: celdas.length };

// Cambiar el return para incluir una 4ta salida
return [ msg_grid, msg_tabla, msg_turno_fin, msg_staffing_config ];
// Retornar mensajes a las 3 salidas del nodo
return [ msg_grid, msg_tabla, msg_turno_fin ];

