func:
// msg.payload = { turno: 1, meta: X, real: Y, ... }
let p = msg.payload;
let category = `T${p.turno}`; // La etiqueta para el eje X, ej: "T1"

// Mensaje para la barra "Real"
let msg_real = {
    topic: "Producci√≥n Real",
    payload: {
        x: category,
        y: p.real
    }
};

// Mensaje para la barra "Meta"
let msg_meta = {
    topic: "Meta de Turno",
    payload: {
        x: category,
        y: p.meta
    }
};

// Retorna un array de mensajes.
// El ui-chart los recibir√° y los plotear√° como dos barras
// en la misma categor√≠a (ej. "T1")
return [msg_real, msg_meta];

// Obtener el array actual de celdas
let celdas = flow.get("celdas") || [];

// Crear una nueva celda
let nuevoId = celdas.length > 0 ? Math.max(...celdas.map(c => c.id)) + 1 : 1;
let nuevaCelda = {
    id: nuevoId,
    estado: "parada",
    unid_ok_hora: 0,
    unid_ok_total_turno: 0,
    tiempo_ciclo: 10 + Math.floor(Math.random() * 10), // Tiempo ciclo aleatorio entre 10-19
    utilizacion: 0,
    tiempo_espera: 0,
    horas_trabajadas: 0
};

// Agregar la nueva celda
celdas.push(nuevaCelda);

// Guardar en el flow
flow.set("celdas", celdas);

// Enviar mensaje para actualizar el grid
let msg_grid = { payload: celdas };
return [msg_grid];

